import React,{useState} from "react";


function CreateArea(props) {
  const [note , setNote] = useState({
    title : "",
    content : ""
  });

  function handleChange(event){
    const {name,value} = event.target;
    setNote(prevNote => {
      return{
        ...prevNote,
        [name] : value
      }
    }) 
  }

  function submitNote(event){
    props.onAdd(note);
    event.preventDefault();

  }

  return (
    <div>
      <form>
        <input name="title" value={note.title} onChage={handleChange} placeholder="Title" />
        <textarea name="content" value={note.content} onChange={handleChange} placeholder="Take a note..." rows="3" />
        <button type="submit" onClick={submitNote}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
