import React from "react";
import Note from "./Note"

function CreateArea() {
  const [note , setNote] = useState({
    title : "",
    content : ""
  });

  function handlelChange(event){
    const {name,value} = event.target;
    setNote(prevNote => {
      return{
        ...prevNote,
        [name] : value
      }
    }) 


  }

  return (
    <div>
      <form>
        <input name="title" value={note.title} onChage={handlelChange} placeholder="Title" />
        <textarea name="content" value={note.content} onChange={handlelChange} placeholder="Take a note..." rows="3" />
        <button type="submit" onClick={addNote}>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
